 141	 const [key, { service, method, data, deserializeReturn }, options] =
 142	 typeof args[0] !== 'string' ? [undefined, ...args] : args;
 143	
 144	 const url = new URL(
 145	 key
 146	 ? `${this.opts.url}/${service}/${key}/${method}`
 147	 : `${this.opts.url}/${service}/${method}`,
 148	 );
 149	
 150	 const headers = new Headers([
 151	 ['content-type', 'application/octet-stream'],
 152	 ['accept', 'application/octet-stream'],
 153	 ]);
 154	 if (options?.idempotencyKey) {
 155	 headers.set('idempotency-key', options.idempotencyKey);
 156	 }
