 65	 @eventDispatcher.listen(onServerMainBootstrap)
 66	 async bootstrap() {
 67	 const config = this.config.server!;
 68	
 69	 for (const object of this.objects) {
 70	 const handlers = this.createObjectHandlers(object);
 71	 this.endpoint.bind(
 72	 restate.object({ name: object.metadata.name, handlers }),
 73	 );
 74	 }
 75	
 76	 for (const service of this.services) {
