 19	 readonly server?: RestateServerConfig;
 20	 readonly ingress?: RestateIngressClientOptions;
 21	 readonly pubsub?: RestatePubSubConfig;
 22	 readonly admin?: RestateAdminClientOptions;
 23	 readonly kafka?: RestateKafkaConfig;
